\alpha(T) = \left\{ \begin{array}{ccc}
\alpha_i &   & T \le T_i \\
\alpha_o + (\alpha_i-\alpha_o) \frac{(T-T_o)^2}{(T_i-T_o)^2} &   & T_i < T < T_o \\
\alpha_o &   & T \ge T_o \end{array} \right\}

def ASR(T, Q=341.3):
    alpha = albedo(T)
    return Q * (1-alpha)

def OLR(T, sigma=5.67E-8, tau=0.57):
    return tau * sigma * T**4

def Ftoa(T):
    return ASR(T) - OLR(T)
